import { useEffect, useState, type FormEvent } from 'react';
import axios from 'axios';
import './App.css';

interface Post {
  id: number;
  name: string;
  message: string;
  created_at: string;
}

function App() {
  const [posts, setPosts] = useState<Post[]>([]);
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const API = '/api/guestbook';

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const res = await axios.get<Post[]>(API);
      setPosts(res.data);
      setError(null);
    } catch (err) {
      if (axios.isAxiosError(err)) {
        setError(err.message);
      } else {
        setError('An unexpected error occurred');
      }
    } finally {
      setLoading(false);
    }
  };

  const submitPost = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const newPost: Partial<Post> = { name, message };

    try {
      const res = await axios.post<Post>(API, newPost);
      setPosts((prevPosts) => [res.data, ...prevPosts]);
      setName('');
      setMessage('');
      setError(null);
    } catch (err) {
      if (axios.isAxiosError(err)) {
        setError(err.message);
      } else {
        setError('An unexpected error occurred');
      }
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className="App">
      <h1>Guestbook</h1>
      <p className="sub-headline">World would appreciate kind words</p>

      <form onSubmit={submitPost}>
        <input
          placeholder="Your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <textarea
          placeholder="Your message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Posting...' : 'Post Entry'}
        </button>
      </form>

      {error && <p className="error">{error}</p>}

      <hr />

      <div className="posts-container">
        {loading && <p>Loading posts...</p>}
        {posts.map((post) => (
          <div className="post" key={post.id}>
            <h3>{post.name}</h3>
            <p>{post.message}</p>
            <small>{new Date(post.created_at).toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;